extension.valueString:
define "SDE Sex":
  case
      when Patient.gender = 'male' then Code { code: 'M', system: 'http://hl7.org/fhir/v3/AdministrativeGender', display: 'Male' }
      when Patient.gender = 'female' then Code { code: 'F', system: 'http://hl7.org/fhir/v3/AdministrativeGender', display: 'Female' }
      else null
    end

/* Initial Population */define "Qualifying Encounters During Measurement Period":
  (AC.QualifiedEncounters([Encounter: Cx."Encounter to Document Medications"])) QualifiedEncounter
    where QualifiedEncounter.period during "Measurement Period"

/* Population Criteria */define "Initial Population":
  exists "Qualifying Encounters During Measurement Period"
    and "Patient Age 18 or Older at Start of Measurement Period"

define "SDE Payer":
  flatten([Coverage: type in "Payer"] Payer
    where Payer.period overlaps "Measurement Period"
      return (
        Payer.type.coding c
          return FHIRHelpers.ToCode(c)
      )
  )
